import React, { useState } from "react";
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { fetchProduct } from "../redux/actions/searchActions";
import './Searchbox.css';



const Searchbox = () => {

    const dispatch = useDispatch();
    const [product_name, set_product_name] = useState('')





    return (

        <div class="input-group rounded">
            <input type="search" class="form-control rounded searchTerm" placeholder="Search" aria-label="Search"
                aria-describedby="search-addon" />

            <Link className="searchButton" to='/search'>
                <span class="input-group-text border-0" id="search-addon" type='submit'
                    value='Buscar'
                    onClick={() => { dispatch(fetchProduct(product_name)) }}>
                    <i class="fas fa-search"></i>
                </span>
            </Link>
        </div>

    )
}

export default Searchbox


